tm(1) = 0;
for l = 1:1950
  tm(l+1) = tm(l) + 0.1;
 end
% /Messdaten

G = 9.81; % Erdbeschleunigung; Für uns konstant;

% Hoffentlich liegen Unterschiede zu Messdaten an diesen Eingabewerten
% Muss weiterhin mit anderen Werten getestet werden
DELTA_T = 0.05; % Zeitintervall
ANFANGSHOEHE = 3500; % Anfangshoehe
CW_MENSCH = 0.78; % Widerstandszahl eines Menschen
CW_FS = 1.33; % Widerstandszahl eines geoeffneten Fallschirmes
RHO_L = 1.294; % Luftdichte; Aenderungen daran muessen nicht beruecksichtigt werden.
A_MENSCH = 0.9; % Flaeche eines Menschen in Fallrichtung
A_FS = 22; % Flaeche eines geoeffneten Fallschirmes
M = 100; % Masse des Menschen (Masse vom Zeugs wie dem Fallschirm mit einbezogen)

H_OEFFNUNG = 2500; % Hoehe an dem Springer den FS oeffnet;

T_OEFFNUNG = 15 % Wie lange in s es braucht den Fallschirm zu oeffnen

t(1) = 0;
v(1) = 0;
s(1) = 0;
a(1) = G;

% --------------------Fall bis zur oeffnung des Fallschirmes--------------------

i = 1;
while s(i) <= H_OEFFNUNG
  t(i+1) = t(i) + DELTA_T;
  v(i+1) = v(i) + (G - (1/2*CW_MENSCH*RHO_L*A_MENSCH*(v(i))^2)/M) * DELTA_T;
  s(i+1) = s(i) + v(i) * DELTA_T;
  a(i+1) = G - (1/2*CW_MENSCH*RHO_L*A_MENSCH*(v(i+1))^2)/M;
  i = i + 1;
end

v_vor_fs = v(i);
t_vor_fs = t(i);

% --------------------Oeffnung des Fallschirmes---------------------------------

% Fliessender Uebergang zwischen cw und Flaeche Mensch zu deren des Fallschirmes
CW_TEMP = 0;
A_TEMP = 0;
k = 1/T_OEFFNUNG;
k_count = 1;
while s(i) <= ANFANGSHOEHE - 10 & k_count < T_OEFFNUNG
  CW_TEMP = CW_MENSCH+((CW_FS-CW_MENSCH) * k * k_count);
  A_TEMP = A_MENSCH+((A_FS-A_MENSCH) * k * k_count);
  t(i+1) = t(i) + DELTA_T;
  v(i+1) = v(i) + (G - (1/2*CW_TEMP*RHO_L*A_TEMP*(v(i))^2)/M) * DELTA_T;
  s(i+1) = s(i) + v(i) * DELTA_T;
  a(i+1) = G - (1/2*CW_TEMP*RHO_L*A_TEMP*(v(i+1))^2)/M;
  i = i + 1;
  k_count = k_count + DELTA_T;
end

% ------------------Völlig geoeffneter Fallschirm-------------------------------

while s(i) <= ANFANGSHOEHE - 10
  t(i+1) = t(i) + DELTA_T;
  v(i+1) = v(i) + (G - (1/2*CW_FS*RHO_L*A_FS*(v(i))^2)/M) * DELTA_T;
  s(i+1) = s(i) + v(i) * DELTA_T;
  a(i+1) = G - (1/2*CW_FS*RHO_L*A_FS*(v(i+1))^2)/M;
  i = i + 1;
end

v_nach_fs = v(i);
t_nach_fs = t(i);

% ----------------------Landephase----------------------------------------------

% warten bis 10m ueber Boden, dann gleichmässig beschleunigte Bewegung
j = i
while t(i) <= t(j) + 10
  t(i+1) = t(i) + DELTA_T;
  v(i+1) = v(i) - v(i)*0.025;
  if (s(i) == ANFANGSHOEHE)
    s(i+1) = ANFANGSHOEHE;
  else
    s(i+1) = s(i) + v(i) * DELTA_T;
  endif
  a(i+1) = (v(i+1)-v(i))/(2*DELTA_T);
  i = i + 1;
end

% ------------------------Ausgabewerte Anzeigen---------------------------------

% Octave scheint aus unbekannten Gründen auch noch T_OEFFNUNG & j auszugeben, sollte kein Problem sein
fprintf("\nv_End Simulation vor dem oeffnen des Fallschirmes: %6.2f m/s, %6.2f km/h\n", v_vor_fs, v_vor_fs*3.6);
fprintf("v_End Simulation nach dem oeffnen des Fallschirmes: %6.2f m/s, %6.2f km/h\n", v_nach_fs, v_nach_fs*3.6);
fprintf("Simulation Zeit vor oeffnen des Fallschirmes: %6.2f s\n", t_vor_fs);
fprintf("Simulation Zeit vor der Landung: %6.2f s\n", t_nach_fs);
fprintf("Fallzeit der Simulation: %6.2f s\n", t(i));
fprintf("Fallzeit der Messdaten: %6.2f s\n", tm(l));
fprintf("Anzahl Schritte zur Abbruchbedingung der Simulation: %1.f\n", i);
fprintf("Anzahl Schritte der Messdaten: %1.f\n", l);

% -----------------------Diagramme auf 2x2 Matrix-------------------------------

subplot(2,2,1); % Oben links
plot(t,s);
grid on
title("s-t-Diagramm", "FontWeight","bold");
xlabel("Zeit in s")
ylabel("Strecke in m")
axis("ij") % dreht die x-Achse um damit die Bewegung nach unten gezeigt wird

subplot(2,2,2); % Oben rechts
plot(t,a);
grid on
title("a-t-Diagramm", "FontWeight","bold");
xlabel("Zeit in s")
ylabel("Beschleunigung in m/s^2")

subplot(2,2,3); % Unten links
plot(t,v);
grid on
title("v-t-Diagramm", "FontWeight","bold");
xlabel("Zeit in s")
ylabel("Geschwindigkeit in m/s")

% Plot der Messdaten/
subplot(2,2,4); % Unten rechts
plot(tm,am);
grid on
title('a-t-Diagramm - Messdaten','FontWeight','bold');
xlabel('Zeit in s');
ylabel('Beschleunigung in m/s ^2');
% /Messdaten
